        public void supprimer(Produit p){
        for(int i=0;i<Produit.produits.size();i++)
        {if(Produit.produits.get(i)==p){Produit.produits.get(i).nbrProd--;Produit.produits.remove(i);i=Produit.produits.size();}}
    
        Produit.nomsS=new String[Produit.produits.size()];
    for(int i=0;i<Produit.produits.size();i++)
    {Produit.nomsS[i]=Produit.produits.get(i).toString();}
    }
    
    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
supprimer(Produit.reverse(jList1.getSelectedValue()));        // TODO add your handling code here:
Accueil.listModel2.removeAllElements();
for(int i=0;i<Produit.nomsS.length;i++)
{
        Accueil.listModel2.addElement(Produit.nomsS[i]);
}
    }//GEN-LAST:event_jButton4ActionPerformed

